import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post's like button to be visible
    await page.waitForSelector('.post:first-child .like-btn');

    // Click the like button on the first post
    await page.click('.post:first-child .like-btn');

    // Wait for the heart icon to become filled
    await page.waitForSelector('.post:first-child .like-btn i.filled');

    // Take screenshot after clicking like
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_like.png'));

    return {
      success: true,
      action: "Clicked like button on the first post and verified heart icon is filled",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

