import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post to be visible
    await page.waitForSelector('.posts .post:first-child', { timeout: 5000 });

    // Click the like button on the first post
    const likeButtonSelector = '.posts .post:first-child .like-btn';
    await page.click(likeButtonSelector);

    // Wait for the heart icon to be filled
    const heartIconSelector = '.posts .post:first-child .like-btn i';
    await page.waitForSelector(`${heartIconSelector}.filled`, { timeout: 5000 });

    // Take screenshot after liking the post
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_like.png'));

    return {
      success: true,
      action: "Successfully clicked the like button on the first post and verified the heart icon is filled.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
