import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for suggestion items to load
    await page.waitForSelector('.suggestion-item');

    // Extract usernames and follower counts
    const extractedData = await page.evaluate(() => {
      const suggestions = Array.from(document.querySelectorAll('.suggestion-item'));
      return suggestions.map(suggestion => {
        const username = suggestion.querySelector('.suggestion-username')?.textContent || '';
        const followerCount = suggestion.querySelector('.sidebar .stats strong')?.textContent || '';
        return { username, followerCount };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extraction.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from suggestion items",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

