import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load
    await page.waitForSelector('.sidebar');

    // Extract usernames and follower counts from suggestion items
    const extractedData = [];
    const suggestionItems = await page.$$('.suggestion-item');

    for (let item of suggestionItems) {
      const username = await item.$eval('.suggestion-username', el => el.textContent.trim());
      const followerCount = await item.$eval('.stats strong', el => el.textContent.trim());
      extractedData.push({ username, followerCount });
    }

    // Log extracted data for verification
    console.log(extractedData);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted_data.png'));

    return {
      success: true,
      action: "Extracted usernames and follower counts from suggestion items",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
