import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggestions to load
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    // Follow and unfollow the first suggested user 5 times
    for (let i = 0; i < 5; i++) {
      const followButton = await page.waitForSelector('.suggestion-item:first-child .follow-btn');
      await followButton.click(); // Follow the user
      await page.waitForTimeout(2000); // Wait for 2 seconds

      const unfollowButton = await page.waitForSelector('.suggestion-item:first-child .follow-btn'); // Re-select the button
      await unfollowButton.click(); // Unfollow the user
      await page.waitForTimeout(2000); // Wait for 2 seconds
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Followed and unfollowed the first suggested user 5 times",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
