import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the explore button in the navigation
    await page.waitForSelector('.explore-btn');
    await page.click('.explore-btn');

    // Wait for the explore modal to open
    await page.waitForSelector('#exploreModal');

    // Take screenshot of the explore modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_explore_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_explore_modal.png'));

    // Verify that the explore modal is visible
    const isModalVisible = await page.isVisible('#exploreModal');

    return {
      success: true,
      action: "Clicked explore button and verified explore modal opened",
      extractedData: { isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

