import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the explore button
    await page.waitForSelector('.explore-btn'); // Using class selector
    await page.click('.explore-btn');

    // Wait for the explore modal to be visible
    await page.waitForSelector('#exploreModal', { state: 'visible' });

    // Take a screenshot of the explore modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_explore_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_explore_modal.png'));

    // Verify that the explore modal is open
    const isModalVisible = await page.isVisible('#exploreModal');

    return {
      success: isModalVisible,
      action: isModalVisible ? "Explore modal opened successfully" : "Failed to open explore modal",
      extractedData: {
        modalVisible: isModalVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
