import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the comment button of the first post
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input to be visible
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button to submit
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.post:first-child .comments'); // Adjust selector as necessary

    // Take a screenshot after the comment is added
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_added.png'));

    // Verify if the comment appears
    const commentVisible = await page.isVisible('.post:first-child .comments:has-text("Great post!")');

    return {
      success: true,
      action: "Comment 'Great post!' added to the first post and verified.",
      extractedData: { commentVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

