import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post's comment button to be available
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    const commentButton = await page.$('.post:first-child .action-btn[aria-label*="Comment"]');
    await commentButton.click();

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput');
    await page.fill('#commentInput', 'Great post!');

    // Wait for the comment submission button to be available and click it
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Take a screenshot after posting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_posted.png'));

    // Verify the comment appears in the comments section
    await page.waitForSelector('.comments-section'); // Assuming there's a container for comments
    const commentExists = await page.$eval('.comments-section', (section) => {
      return section.innerText.includes('Great post!');
    });

    return {
      success: true,
      action: "Added comment 'Great post!' to the first post and verified it appeared",
      extractedData: { commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
