import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the posts to load
    await page.waitForSelector('.post');

    // Get all visible like buttons and click them
    const likeButtons = await page.$$('.post .like-btn.i.far.fa-heart');
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(500); // Wait for a short duration to avoid being flagged as a bot
    }

    // Take screenshot after liking posts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_liked_posts.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_liked_posts.png'));

    return {
      success: true,
      action: "Liked all visible posts in the feed",
      extractedData: { likedPostsCount: likeButtons.length },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

