import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the create button in navigation
    await page.waitForSelector('.create-btn');
    await page.click('.create-btn');

    // Wait for the create post modal to open
    await page.waitForSelector('#createPostModal');

    // Take screenshot of the create post modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_create_post_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_create_post_modal.png'));

    // Verify that the create post modal is visible
    const isModalVisible = await page.isVisible('#createPostModal');

    return {
      success: true,
      action: "Clicked create button and verified create post modal opened",
      extractedData: { modalVisible: isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

