import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click "Your story" button
    await page.waitForSelector('.story'); // Assuming the "Your story" button is represented by a class 'story'
    await page.click('.story');

    // Wait for the create story modal to open
    await page.waitForSelector('#createPostModal'); // Assuming the modal has an ID 'createPostModal'

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_create_story_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_create_story_modal.png'));

    // Verify that the modal is visible
    const isModalVisible = await page.isVisible('#createPostModal');

    return {
      success: true,
      action: "Clicked 'Your story' button and verified create story modal opened",
      extractedData: { isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

