import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post's comment button to be visible and click it
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input field to be visible
    await page.waitForSelector('#commentInput');

    // Type the comment "Great post!" into the comment input
    await page.fill('#commentInput', 'Great post!');

    // Wait for the comment button and click it to submit the comment
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.post:first-child .comments'); // Adjust selector if necessary

    // Take a screenshot after posting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_posted.png'));

    // Verify that the comment appears
    const commentsSection = await page.$eval('.post:first-child .comments', el => el.innerText);
    const commentPosted = commentsSection.includes('Great post!');

    return {
      success: commentPosted,
      action: commentPosted ? "Comment added successfully" : "Comment did not appear",
      extractedData: { commentPosted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
