import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggestions to load
    await page.waitForSelector('.suggestion-item:first-child');

    // Follow and unfollow the first user in suggestions 5 times
    for (let i = 0; i < 5; i++) {
      // Click the follow button
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for 1 second

      // Take screenshot after following
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_follow_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_follow_${i + 1}.png`));

      // Click the unfollow button
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for 1 second

      // Take screenshot after unfollowing
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_unfollow_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_3_unfollow_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Successfully followed and unfollowed the first user in suggestions 5 times",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

