import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post to be visible and click the like button
    await page.waitForSelector('.post:first-child .like-btn');
    await page.click('.post:first-child .like-btn');

    // Wait for a moment to allow the like action to complete
    await page.waitForTimeout(1000);

    // Verify that the heart icon is filled
    const isHeartFilled = await page.evaluate(() => {
      const heartIcon = document.querySelector('.post:first-child .like-btn i');
      return heartIcon.classList.contains('filled'); // Assuming 'filled' class indicates a liked state
    });

    // Take a screenshot after liking the post
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_liked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_liked.png'));

    return {
      success: true,
      action: "Clicked like button on the first post in feed and verified heart icon",
      extractedData: { isHeartFilled },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
