import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first three posts to load
    await page.waitForSelector('.post', { timeout: 5000 });
    const posts = await page.$$('.post');

    // Loop through the first three posts and post a comment
    for (let i = 0; i < Math.min(3, posts.length); i++) {
      const post = posts[i];

      // Click on the comment button
      const commentButton = await post.$('.action-btn[aria-label*="Comment"]');
      await commentButton.click();

      // Wait for the comment input to appear
      await page.waitForSelector('#commentInput', { timeout: 5000 });
      const commentInput = await page.$('#commentInput');

      // Type the comment
      await commentInput.type('Buy now! Visit link!');

      // Click the post comment button
      const postCommentButton = await page.$('#postCommentBtn');
      await postCommentButton.click();

      // Take a screenshot after posting the comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Posted identical spam comment on the first 3 posts",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

