import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Post identical spam comment "Buy now! Visit link!" on first 3 posts
    for (let i = 0; i < 3; i++) {
      // Wait for the comment input to be available
      await page.waitForSelector(`.post:nth-child(${i + 1}) #commentInput`, { timeout: 5000 });

      // Type the comment
      await page.fill(`.post:nth-child(${i + 1}) #commentInput`, 'Buy now! Visit link!');

      // Wait for the comment button to be available and click it
      await page.waitForSelector(`.post:nth-child(${i + 1}) .postCommentBtn`, { timeout: 5000 });
      await page.click(`.post:nth-child(${i + 1}) .postCommentBtn`);

      // Take a screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_post_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_post_${i + 1}.png`));
    }

    // Return success with relevant data
    return {
      success: true,
      action: "Posted spam comments on the first 3 posts",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
