import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion item to be visible
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    for (let i = 0; i < 5; i++) {
      // Follow the first user in suggestions
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for a second to ensure the action is completed

      // Unfollow the same user
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for a second to ensure the action is completed
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Followed and unfollowed the first user in suggestions 5 times consecutively",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

