import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion item to load
    await page.waitForSelector('.suggestion-item:first-child');

    // Click the follow button on the first suggestion
    const followButtonSelector = '.suggestion-item:first-child .follow-btn';
    await page.waitForSelector(followButtonSelector);
    await page.click(followButtonSelector);

    // Wait for the button text to change to "Following"
    await page.waitForFunction(
      (selector) => document.querySelector(selector).innerText === 'Following',
      {},
      followButtonSelector
    );

    // Take a screenshot after clicking follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    return {
      success: true,
      action: "Clicked follow button on the first user in suggestions and verified text changed to 'Following'",
      extractedData: { buttonText: await page.textContent(followButtonSelector) },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
