import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first post's comment button
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input to be visible
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button to submit
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.comments-section'); // Assuming there's a class for comments section
    const commentExists = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.comments-section .comment')).some(comment => comment.textContent.includes('Great post!'));
    });

    // Take screenshot after commenting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_added.png'));

    return {
      success: true,
      action: "Added comment 'Great post!' to the first post and verified it appears in the comments section.",
      extractedData: { commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

