    // Click on the first post's comment button
    await page.waitForSelector('.posts .post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.posts .post:first-child .action-btn[aria-label*="Comment"]');
    await page.waitForTimeout(500); // Wait for comment input to appear

