import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion item to be visible
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    // Click the follow button on the first user in suggestions
    await page.click('.suggestion-item:first-child .follow-btn');

    // Wait for the button text to change to "Following"
    await page.waitForFunction(() => {
      const button = document.querySelector('.suggestion-item:first-child .follow-btn');
      return button && button.textContent === 'Following';
    });

    // Take a screenshot after the follow action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following.png'));

    return {
      success: true,
      action: "Clicked follow button on first user in suggestions and verified button text changed to 'Following'",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

