import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract hashtags and like counts
    const posts = await page.$$('.post');
    const extractedData = [];

    for (const post of posts) {
      const captionElement = await post.$('.caption-text');
      const likesElement = await post.$('.post-likes');

      const caption = captionElement ? await captionElement.innerText() : '';
      const likes = likesElement ? await likesElement.innerText() : '';

      // Extract hashtags from the caption
      const hashtags = caption.match(/#\w+/g) || [];

      extractedData.push({
        hashtags,
        likes
      });
    }

    console.log(extractedData);

    return {
      success: true,
      action: "Extracted hashtags and like counts from posts",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract hashtags from post captions
    const postCaptions = await page.$$eval('.caption-text', captions => {
      return captions.map(caption => {
        const text = caption.innerText;
        const hashtags = text.match(/#[\w]+/g); // Extract hashtags
        return hashtags ? hashtags : [];
      });
    });

    // Extract like counts
    const likeCounts = await page.$$eval('.post-likes', likes => {
      return likes.map(like => parseInt(like.innerText.replace(/,/g, ''), 10)); // Convert like text to integer
    });

    // Log the extracted data
    console.log("Extracted Hashtags:", postCaptions);
    console.log("Extracted Like Counts:", likeCounts);

    return {
      success: true,
      action: "Extracted hashtags and like counts from post captions",
      extractedData: {
        hashtags: postCaptions,
        likes: likeCounts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for post captions and likes to load
    await page.waitForSelector('.post-caption .caption-text');
    await page.waitForSelector('.post-likes');

    // Extract hashtags and like counts
    const hashtags = await page.$$eval('.post-caption .caption-text', captions => {
      return captions.map(caption => {
        const matches = caption.innerText.match(/#\w+/g);
        return matches ? matches : [];
      }).flat();
    });

    const likeCounts = await page.$$eval('.post-likes', likes => {
      return likes.map(like => like.innerText.trim());
    });

    console.log('Extracted Hashtags:', hashtags);
    console.log('Extracted Like Counts:', likeCounts);

    return {
      success: true,
      action: "Extracted hashtags and like counts from post captions",
      extractedData: { hashtags, likeCounts },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
