import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for posts to load
    await page.waitForSelector('.post');

    // Get all visible like buttons
    const likeButtons = await page.$$('.post .like-btn');

    // Click each like button
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(500); // Wait for a short duration to avoid overwhelming the server
    }

    // Take final screenshot after liking posts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_liking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_liking.png'));

    return {
      success: true,
      action: "Liked all visible posts in the feed",
      extractedData: {
        likedPostsCount: likeButtons.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for posts to be visible
    await page.waitForSelector('.posts');

    // Get all visible like buttons
    const likeButtons = await page.$$('.post .like-btn');

    for (const button of likeButtons) {
      // Check if the button is visible and not already liked
      const isVisible = await button.isIntersectingViewport();
      const isLiked = await button.evaluate(btn => btn.classList.contains('liked'));

      if (isVisible && !isLiked) {
        // Click the like button
        await button.click();
        // Optional: Take a screenshot of each like action
        await page.screenshot({ path: path.join(screenshotsDir, 'step_liked_post.png') });
        screenshots.push(path.join(screenshotsDir, 'step_liked_post.png'));
      }
    }

    return {
      success: true,
      action: "Liked all visible posts in the feed",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

