    screenshots.push(path.join(screenshotsDir, 'step_3_settings_modal.png'));

    // Toggle the notification setting switch
    await page.waitForSelector('#notificationsToggle');
    const toggleChecked = await page.isChecked('#notificationsToggle');
    await page.click('#notificationsToggle'); // Toggle the switch

    // Validate the toggle state
    const toggleState = toggleChecked ? 'off' : 'on';

    // Take a screenshot after toggling
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_toggle_notifications.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_toggle_notifications.png'));

    return {
      success: true,
      action: `Toggled notification setting to ${toggleState}.`,
      extractedData: { toggleState },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
