    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Locate the first 3 posts
    const posts = await page.$$('article div[role="presentation"]'); // This selects posts based on their presentation role
    for (let i = 0; i < Math.min(3, posts.length); i++) {
      const post = posts[i];
      await post.click(); // Click the post to open it

      // Wait for the comment input field to be visible
      await page.waitForSelector('#commentInput');

      // Type the spam comment
      await page.fill('#commentInput', 'Buy now! Visit link!');

      // Click the comment button
      await page.click('button[aria-label="Post"]'); // Assuming this button has an aria-label for posting comments

      // Take a screenshot after posting the comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`));

      // Go back to the main feed
      await page.goBack();
    }

    return {
      success: true,
      action: "Posted identical spam comment on the first 3 posts",
      extractedData: {},
