import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the comment button of the first post
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input to be visible and type the comment
    await page.waitForSelector('#commentInput');
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button to submit the comment
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.comments-section'); // Assuming there's a class for comments section
    const commentExists = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.comments-section .comment')).some(comment => comment.textContent.includes('Great post!'));
    });

    // Take a screenshot after adding the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_added.png'));

    return {
      success: commentExists,
      action: commentExists ? "Comment added successfully" : "Comment not found in the comments section",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
