import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the bookmark button on the first post
    await page.waitForSelector('.post:first-child .bookmark-btn');
    await page.click('.post:first-child .bookmark-btn');

    // Wait for the bookmark icon to become filled
    await page.waitForSelector('.post:first-child .bookmark-btn i.filled'); // Assuming filled icon has a class 'filled'

    // Take screenshot after clicking the bookmark
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Extract meaningful data to verify task completion
    const isBookmarked = await page.$eval('.post:first-child .bookmark-btn i', el => el.classList.contains('filled'));

    return {
      success: true,
      action: "Clicked bookmark button on the first post and verified bookmark icon is filled",
      extractedData: { isBookmarked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

