import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the profile button to open the profile modal
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');
    await page.waitForSelector('#profileModal');

    // Take a screenshot of the profile modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_modal.png'));

    // Click on the settings button within the profile modal
    await page.waitForSelector('.settings-btn');
    await page.click('.settings-btn');
    await page.waitForSelector('#settingsModal');

    // Take a screenshot of the settings modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_settings_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_settings_modal.png'));

    // Toggle the notifications setting
    await page.waitForSelector('#notificationsToggle');
    await page.click('#notificationsToggle');

    // Take a screenshot after toggling the notification setting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_notifications_toggled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_notifications_toggled.png'));

    // Verify the toggle state (this can be adjusted based on the expected state)
    const toggleState = await page.$eval('#notificationsToggle', el => el.checked);

    return {
      success: true,
      action: "Toggled the notification setting in the profile modal",
      extractedData: { toggleState },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

