import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the post elements to load
    await page.waitForSelector('.post');

    // Get the first three posts
    const posts = await page.$$('.post');

    // Loop through the first three posts to post the comment
    for (let i = 0; i < Math.min(posts.length, 3); i++) {
      const post = posts[i];

      // Click on the comment button
      await post.hover(); // Hovering to ensure the comment button is visible
      const commentButton = await post.$('.action-btn[aria-label*="Comment"]');
      if (commentButton) {
        await commentButton.click();
      }

      // Wait for the comment input to appear
      await page.waitForSelector('#commentInput');

      // Type the comment
      await page.fill('#commentInput', 'Buy now! Visit link!');

      // Click the post comment button
      const postCommentButton = await page.$('#postCommentBtn');
      if (postCommentButton) {
        await postCommentButton.click();
      }

      // Wait for a moment for the comment to be posted
      await page.waitForTimeout(2000);
    }

    // Take a final screenshot after posting comments
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_comments.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_comments.png'));

    return {
      success: true,
      action: "Posted identical spam comments on the first 3 posts",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
