import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion user to be visible
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    // Follow and unfollow the first user in suggestions 5 times
    for (let i = 0; i < 5; i++) {
      // Click the follow button
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for 1 second

      // Click the unfollow button
      await page.click('.suggestion-item:first-child .follow-btn');
      await page.waitForTimeout(1000); // Wait for 1 second
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Followed and unfollowed the first user in suggestions 5 times.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Follow and unfollow the first user in suggestions 5 times
    for (let i = 0; i < 5; i++) {
      // Wait for the first suggestion to be visible
      await page.waitForSelector('.suggestion-item:first-child .follow-btn', { timeout: 5000 });

      // Click on the "Follow" button
      const followButton = await page.$('.suggestion-item:first-child .follow-btn');
      if (followButton) {
        await followButton.click();
        await page.waitForTimeout(1000); // wait for 1 second for the action to complete
      }

      // Take screenshot after following
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_follow_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_follow_${i + 1}.png`));

      // Click on the "Unfollow" button
      const unfollowButton = await page.$('.suggestion-item:first-child .follow-btn'); // Assume the button text changes to "Unfollow"
      if (unfollowButton) {
        await unfollowButton.click();
        await page.waitForTimeout(1000); // wait for 1 second for the action to complete
      }

      // Take screenshot after unfollowing
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_unfollow_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_3_unfollow_${i + 1}.png`));
    }

    return {
      success: true,
      action: "Successfully followed and unfollowed the first user in suggestions 5 times.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

