import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first post's comment button
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input field to become visible
    await page.waitForSelector('#commentInput');
    await page.fill('#commentInput', 'Great post!');

    // Click on the submit comment button
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForTimeout(1000); // Give some time for the comment to be processed
    const commentExists = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.comments .comment')).some(comment => comment.textContent.includes('Great post!'));
    });

    // Take screenshot after posting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_posted.png'));

    return {
      success: true,
      action: "Posted a comment on the first post and verified its appearance",
      extractedData: { commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
