import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the like buttons to load
    await page.waitForSelector('.post');

    // Select all visible posts
    const posts = await page.$$('.post');

    // Iterate through each post and click the like button
    for (const post of posts) {
      // Wait for the like button within the post
      const likeButton = await post.$('i.far.fa-heart');
      if (likeButton) {
        await likeButton.click();
        // Take a screenshot after each like action
        await page.screenshot({ path: path.join(screenshotsDir, `liked_post_${posts.indexOf(post) + 1}.png`) });
        screenshots.push(path.join(screenshotsDir, `liked_post_${posts.indexOf(post) + 1}.png`));
      }
    }

    return {
      success: true,
      action: "Successfully liked all visible posts",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
