import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for post captions and likes to load
    await page.waitForSelector('.post-caption');
    await page.waitForSelector('.post-likes');

    // Extract hashtags and like counts
    const hashtags = await page.$$eval('.post-caption', captions => {
      return captions.map(caption => {
        const matches = caption.innerText.match(/#\w+/g);
        return matches ? matches : [];
      }).flat();
    });

    const likeCounts = await page.$$eval('.post-likes', likes => {
      return likes.map(like => like.innerText);
    });

    // Log extracted data to console
    console.log('Extracted Hashtags:', hashtags);
    console.log('Extracted Like Counts:', likeCounts);

    return {
      success: true,
      action: "Extracted hashtags from post captions and like counts",
      extractedData: {
        hashtags,
        likeCounts
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

