import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the bookmark button on the first post
    await page.waitForSelector('.post:first-child .bookmark-btn');
    await page.click('.post:first-child .bookmark-btn');

    // Wait for the bookmark icon to become filled
    await page.waitForSelector('.post:first-child .bookmark-btn i.filled', { timeout: 5000 });

    // Take a screenshot after clicking the bookmark
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Verify that the bookmark icon has changed to filled
    const isBookmarked = await page.$eval('.post:first-child .bookmark-btn i', el => el.classList.contains('filled'));

    return {
      success: true,
      action: "Clicked bookmark button on first post and verified bookmark icon became filled",
      extractedData: { isBookmarked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

