import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion user to be visible
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');

    // Click the follow button on the first user in suggestions
    await page.click('.suggestion-item:first-child .follow-btn');

    // Wait for the button text to change to "Following"
    await page.waitForFunction(() => {
      const button = document.querySelector('.suggestion-item:first-child .follow-btn');
      return button && button.textContent.includes('Following');
    });

    // Take a screenshot after clicking follow
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_clicked.png'));

    return {
      success: true,
      action: "Clicked follow button on first user in suggestions and verified text change to 'Following'",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the suggestion items to load
    await page.waitForSelector('.suggestion-item:first-child');

    // Click the follow button on the first user in suggestions
    const followButtonSelector = '.suggestion-item:first-child .follow-btn';
    await page.waitForSelector(followButtonSelector);
    await page.click(followButtonSelector);

    // Take a screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Wait for the button text to change to "Following"
    await page.waitForFunction(
      (selector) => document.querySelector(selector)?.innerText === 'Following',
      {},
      followButtonSelector
    );

    // Verify the button text has changed
    const buttonText = await page.innerText(followButtonSelector);

    return {
      success: true,
      action: "Clicked follow button and verified text changed to 'Following'",
      extractedData: { buttonText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first suggestion button to be visible and click it
    await page.waitForSelector('.suggestion-item:first-child .follow-btn');
    await page.click('.suggestion-item:first-child .follow-btn');

    // Wait for the button to update and verify it changes to "Following"
    await page.waitForFunction(() => {
      const buttonText = document.querySelector('.suggestion-item:first-child .follow-btn').textContent;
      return buttonText === 'Following';
    });

    // Take a screenshot after the action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following.png') });
