    return {
      success: true,
      action: "Clicked follow button on the first user in suggestions and verified button text changed to 'Following'",
      extractedData: {
        buttonText: await page.evaluate(() => document.querySelector('.suggestion-item:first-child .follow-btn').textContent)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
