import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the create button in navigation
    await page.waitForSelector('.create-btn'); // Using class selector for the create button
    await page.click('.create-btn'); // Click on the create button
    await page.waitForTimeout(2000); // Wait for the modal to load

    // Verify that the create post modal opens
    const modalVisible = await page.isVisible('#createPostModal'); // Using ID selector for modal
    if (!modalVisible) {
      throw new Error('Create post modal did not open.');
    }

    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    return {
      success: true,
      action: "Clicked create button and verified create post modal opened",
      extractedData: { modalOpened: true },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
