import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the "Your story" button
    await page.waitForSelector('button[class*="your-story-btn"]', { timeout: 5000 });
    await page.click('button[class*="your-story-btn"]');

    // Wait for the create story modal to open
    await page.waitForSelector('.create-story-modal', { timeout: 5000 });

    // Verify that the create story modal is visible
    const isModalVisible = await page.isVisible('.create-story-modal');
    if (!isModalVisible) {
      throw new Error('Create story modal did not open');
    }

    // Take a screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    return {
      success: true,
      action: "Clicked 'Your story' button and verified create story modal opened",
      extractedData: { modalVisible: isModalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
