import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post to be visible
    await page.waitForSelector('.posts .post:first-child');

    // Click on the like button of the first post
    await page.click('.posts .post:first-child .like-btn');

    // Take a screenshot after clicking the like button
    await page.waitForTimeout(500); // wait for the animation to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_like.png'));

    // Verify the heart icon is filled (change of class or style)
    const isLiked = await page.evaluate(() => {
      const heartIcon = document.querySelector('.posts .post:first-child .like-btn i');
      return heartIcon && heartIcon.classList.contains('liked'); // assuming the class 'liked' indicates a filled heart
    });

    return {
      success: true,
      action: "Clicked like button on the first post and verified heart icon filled",
      extractedData: { isLiked },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
