import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first post's comment button
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input to appear
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great post!');

    // Click the post comment button
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.post:first-child .comments-section'); // Adjust selector as necessary
    const comments = await page.$$eval('.post:first-child .comments-section .comment', comments => comments.map(comment => comment.textContent));

    // Take a screenshot after posting the comment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_posted.png'));

    // Verify the comment appears in the comments section
    const commentPosted = comments.includes('Great post!');

    return {
      success: true,
      action: "Comment 'Great post!' added to the first post and verified.",
      extractedData: { commentPosted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

