    await page.waitForSelector('#commentInput');

    // Type the comment "Great post!"
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to be added in the comments section
    await page.waitForSelector('.post:first-child .comment:has-text("Great post!")');

