    return {
      success: true,
      action: "Added comment 'Great post!' to the first post and verified its appearance",
      extractedData: { commentPosted: commentExists },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
