import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first post's share button to be visible and click it
    await page.waitForSelector('.post:first-child .share-btn');
    await page.click('.post:first-child .share-btn');

    // Wait for share options to appear
    await page.waitForSelector('.share-options'); // Assuming there's a class for share options

    // Take screenshot after clicking share button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_share_options.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_share_options.png'));

    // Verify that share options are visible
    const shareOptionsVisible = await page.isVisible('.share-options');

    return {
      success: true,
      action: "Clicked share button on first post and verified share options appear",
      extractedData: { shareOptionsVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

