import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open comments on the first post
    await page.waitForSelector('.post:first-child .view-comments');
    await page.click('.post:first-child .view-comments');
    await page.waitForLoadState('networkidle');

    // Take screenshot after opening comments
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comments_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comments_opened.png'));

    // Click reply on the first comment
    await page.waitForSelector('#commentsModal');
    await page.waitForSelector('.comment-action[onclick*="replyToComment"]');
    await page.click('.comment-action[onclick*="replyToComment"]');

    // Take final screenshot after clicking reply
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_reply_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_reply_clicked.png'));

    return {
      success: true,
      action: "Opened comments on the first post and clicked reply on the first comment",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

