import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the posts to load
    await page.waitForSelector('.post');

    // Get the first three posts
    const posts = await page.$$('.post');
    for (let i = 0; i < 3; i++) {
      if (posts[i]) {
        // Click on the comment button
        await posts[i].$('.action-btn[aria-label*="Comment"]').then(button => button.click());

        // Wait for the comment input to be visible
        await page.waitForSelector('#commentInput');

        // Type the comment
        await page.fill('#commentInput', 'Buy now! Visit link!');

        // Click the post comment button
        await page.click('#postCommentBtn');

        // Take a screenshot after posting the comment
        await page.screenshot({ path: path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`) });
        screenshots.push(path.join(screenshotsDir, `step_2_posted_comment_${i + 1}.png`));
      }
    }

    return {
      success: true,
      action: "Posted identical spam comments on the first 3 posts",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

