import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first user suggestion to load
    await page.waitForSelector('.suggestion-item:first-child');

    // Loop to follow and unfollow the first user 5 times
    for (let i = 0; i < 5; i++) {
      // Follow the first suggestion
      const followButton = await page.$('.suggestion-item:first-child .follow-btn');
      if (followButton) {
        await followButton.click();
        await page.waitForTimeout(2000); // Wait for 2 seconds
      }

      // Unfollow the first suggestion
      const unfollowButton = await page.$('.suggestion-item:first-child .follow-btn'); // Assuming the button changes to unfollow
      if (unfollowButton) {
        await unfollowButton.click();
        await page.waitForTimeout(2000); // Wait for 2 seconds
      }
    }

    // Take a screenshot after the operations
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_follow_unfollow.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_follow_unfollow.png'));

    return {
      success: true,
      action: "Successfully followed and unfollowed the first user 5 times.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
