import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for posts to load
    await page.waitForSelector('.posts');

    // Get all like buttons for visible posts
    const likeButtons = await page.$$('.post .like-btn');

    // Click each like button
    for (let button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Wait for a short period to avoid overwhelming the server
    }

    // Take screenshot after liking posts
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_liking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_liking.png'));

    return {
      success: true,
      action: "Liked all visible posts in the feed",
      extractedData: { likedPostsCount: likeButtons.length },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

