import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first post's comment button
    await page.waitForSelector('.post:first-child .action-btn[aria-label*="Comment"]');
    await page.click('.post:first-child .action-btn[aria-label*="Comment"]');

    // Wait for the comment input field to appear
    await page.waitForSelector('#commentInput');

    // Type the comment
    await page.fill('#commentInput', 'Great post!');

    // Click the comment button to submit
    await page.waitForSelector('#postCommentBtn');
    await page.click('#postCommentBtn');

    // Wait for the comment to appear in the comments section
    await page.waitForSelector('.post:first-child .comment', { timeout: 5000 });

    // Take a screenshot after commenting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_comment.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_comment.png'));

    // Extract the comment to verify it appears
    const commentText = await page.textContent('.post:first-child .comment');

    return {
      success: true,
      action: "Comment 'Great post!' added to the first post and verified",
      extractedData: { commentText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

