import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open profile modal
    await page.waitForSelector('.profile-btn'); // Wait for profile button
    await page.click('.profile-btn'); // Click profile button
    await page.waitForSelector('#profileModal'); // Wait for profile modal to appear

    // Access settings
    await page.waitForSelector('.settings-btn'); // Wait for settings button
    await page.click('.settings-btn'); // Click settings button
    await page.waitForSelector('#settingsModal'); // Wait for settings modal to appear

    // Toggle notification setting
    await page.waitForSelector('#notificationsToggle'); // Wait for notifications toggle
    const toggleStatus = await page.$eval('#notificationsToggle', el => el.checked); // Check current toggle status
    await page.click('#notificationsToggle'); // Click to toggle notification setting
    const newToggleStatus = !toggleStatus; // Determine new status after toggle

    // Take screenshot after toggling
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_toggle_notifications.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_toggle_notifications.png'));

    return {
      success: true,
      action: `Notification setting toggled to ${newToggleStatus ? 'enabled' : 'disabled'}`,
      extractedData: { notificationsEnabled: newToggleStatus },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
